mapper.select:
select

from t_user_account
where account = #{account,jdbcType=VARCHAR}
AND password = #{password,jdbcType=VARCHAR}
AND is_deleted = 0

mapper.update:
UPDATE t_user_account
SET vip_expire_time = #{vipExpireTime,jdbcType=TIMESTAMP},
    accumulate_vip_day = accumulate_vip_day + #{accumulateVipDay,jdbcType=INTEGER}
WHERE account = #{account,jdbcType=VARCHAR}

UPDATE t_user_account
SET plugin_vip_expire_time = #{pluginVipExpireTime,jdbcType=TIMESTAMP},
    plugin_accumulate_vip_day = plugin_accumulate_vip_day + #{pluginAccumulateVipDay,jdbcType=INTEGER}
WHERE account = #{account,jdbcType=VARCHAR}
